mapper.select:
SELECT id, code, name, type_id
FROM t_sys_dictionary
WHERE id = #{id}

mapper.update:
UPDATE t_sys_dictionary
SET code = #{code}, name = #{name}, modify_time = #{modifyTime}
WHERE id = #{id}

UPDATE t_sys_dictionary
SET deleted = 1
WHERE id = #{id}

UPDATE t_sys_dictionary
SET deleted = 1
WHERE type_id = #{typeId}
